change_filament_gcode:
{if next_extruder == 0}
M104 S{nozzle_standby_temperature[1]} T1
T0
{if nozzle_temperature[0] == 0}
CLEAN_PURGE T=0 S={nozzle_temperature_initial_layer[0]}
{else}
CLEAN_PURGE T=0 S={nozzle_temperature[0]}
{endif}
{elsif next_extruder == 1}
M104 S{nozzle_standby_temperature[0]} T0
T1
{if nozzle_temperature[1] == 0}
CLEAN_PURGE T=1 S={nozzle_temperature_initial_layer[1]}
{else}
CLEAN_PURGE T=1 S={nozzle_temperature[1]}
{endif}
{endif}
